import styled from 'styled-components';


export const InputContainern = styled.div`
    width: 100%;
    height: 75px;
    background-color: #AAFFAA;

    display: flex;
    align-items: center;
    justify-content:flex-end;

    font-size: 24px;
    font-family: 'Roboto';
    padding: 0 10px;

    input{
        width: 100%;
    height: 75px;
    background-color: #AAFFAA;
    border:0;
    flex-direction: column;
    align-items: flex-end;

    font-size: 24px;
    font-family: 'Roboto';

    }


`
